import React, {Fragment, useEffect} from 'react'
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from "prop-types";
//Actions
import { _setAlert } from '../actions/alert';
import { _likeShop } from '../actions/shop';



const ShopItem = (props) => {

    const { shop, preferred, isAuthentificated, _setAlert, _likeShop, status } = props






    /**
     * Like a shop
     * if isAuthentificated: false => call _setAlert() 
     * else call _likeShop()
     */
    const onLiked = async (e) => {

       // e.preventDefault();

        try {
            if (!isAuthentificated) {
                _setAlert('Unauthorized: You must be connected', 'warning');

                await _likeShop(shop._id)


            }
            if (isAuthentificated) {
                _likeShop(shop.id)
            }



        } catch (error) {

        }

    }



    const renderButton = ()=>{
        if(preferred){
            //& nbsp;& nbsp;
            return (
                <Fragment>
                    <button type="button" className="btn btn-danger btn-sm" >Remove</button> 
                    &nbsp;&nbsp;
                </Fragment>
             )

        }else{
            return( 
                <Fragment>
                    <button type="button" className="btn btn-danger btn-sm" >Dislike</button>
                                        &nbsp;&nbsp;

                    <button type="button" className="btn btn-primary btn-sm" onClick={(e) => onLiked(e)} >Like</button>
                </Fragment>
            )
        }
    }


    return (
        <div className="col-sm-6 col-lg-3 mb-3" data-aos="fade-up">

            <div className="block-4 text-center border">

                <figure className="block-4-image">
                    <Link to="#">
                        <img src={`images/${shop.imageURL}`} alt="placeholder" className="img-fluid" />
                    </Link>
                </figure>

                <div className="block-4-text p-4">
                    <h6><a href="shop-single.html">{shop.name}</a></h6>
                    <br/>
                    {renderButton()}

                </div>
            </div>
        </div>
    )
}
ShopItem.propTypes = {
  isAuthentificated: PropTypes.bool,
  _setAlert: PropTypes.func.isRequired,
  _likeShop: PropTypes.func.isRequired,
  status: PropTypes.object,
};

// mappons le state dans les props du component.
const mapStateToProps = state => ({
    isAuthentificated: state.auth.isAuthentificated, 
    status: state.shop.status
});

export default connect(mapStateToProps, {_setAlert, _likeShop})(ShopItem);
