import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom'
import { connect } from "react-redux";

import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,
  NavLink,
} from "reactstrap";
//Components
import AlertNotification from '../layout/AlertNotification';
//Actions : _xxYYY




const LoginModal = (props) => {



    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formModalData, setFormModalData] = useState({
        name: '',
        email: '',
        password: '',

    });


    // const { email, password } = this.state;
    const { email, password } = formModalData;

    const toggle = () => {
        //clear error action meth.
        setFormModalData({
            ...formModalData,
        });
    };

    //Maj state input fields text on change
    const onChange = (e) => {
        setFormModalData({
          ...formModalData,
          [e.target.name]: e.target.value
        });
    };


    const onSubmit = async (e) => {
        e.preventDefault();


    }




    return (

        <Fragment>
            <div>
                <NavLink onClick={()=>toggle()} href="#">Login</NavLink>

                <Modal isOpen={formModalData.modal} toggle={()=>toggle()} >

                    <ModalHeader toggle={()=>toggle()}> Login </ModalHeader>

                    <ModalBody>

                        <AlertNotification/>

                        <Form onSubmit={e=>onSubmit(e)}>
                            <FormGroup>

                                <Label for="email">Email</Label>
                                <Input
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="Email"
                                    onChange={(e)=>onChange(e) }
                                />

                                <Label for="name">Password</Label>
                                <Input
                                    type="password"
                                    name="password"
                                    id="password"
                                    placeholder="Password"
                                    onChange={(e)=>onChange(e)}
                                />
                                <Button
                                    color="primary"
                                    block={true}
                                >
                                    LOGIN
                                </Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        </Fragment>

    );


}


//1- mapons le state dans les props

const mapStateToProps = state => ({
    alerts: state.alert
});

export default connect(mapStateToProps, null)(LoginModal);
