import {
  GET_DEFAULT_LIST_SHOPS,
  GET_PREFERRED_SHOPS,
  GET_NEARBY_SHOPS,
  REMOVE_SHOPS_USER_RELATED,
  ERROR_SHOPS
} from "../actions/types";

const initialState = {
    default_list_shops: [],
    preferred_shops: [],
    nearby_shops: [], 
    loading:true,
    errors: {}
};

export default function (state = initialState, action) {

    let nextState;

    const { type, payload } = action;

    switch (type) {

     case GET_DEFAULT_LIST_SHOPS:
        return nextState = {
                ...state,
            loading: false,
            default_list_shops: payload
        };

      case GET_NEARBY_SHOPS:
        return nextState = {
                ...state,
            loading: false,
            nearby_shops: payload
        };

      case GET_PREFERRED_SHOPS:
        return nextState ={
            ...state,
            loading: false,
            preferred_shops: payload
        };

     case REMOVE_SHOPS_USER_RELATED:
        return nextState = {
            ...state,
            preferred_shops: null,
            nearby_shops: null,
            loading:false
        };
     case ERROR_SHOPS:
        return nextState = {
            ...state,
            preferred_shops: null,
            nearby_shops: null,
            loading:false
        };
      default:
        return nextState || state;
    }


}
