import React,{Fragment} from 'react'
import { Alert } from "reactstrap";
import PropTypes from "prop-types";
import { connect } from "react-redux";

//2- Destructuring des du state placé en props.

const AlertBar = ({ alerts }) => {

       if (alerts !== null && alerts.length > 0) {

          return alerts.map(alert => (
            <Alert key={alert.id} color={alert.alertType}>
              {alert.msg}
            </Alert>
          ));

       }else{
           return null
       }



}


Alert.propTypes = {
    alerts: PropTypes.array.isRequired
}

//1- mappons le state dans les props et appelons le alerts

const mapStateToProps = state => ({
    alerts: state.alert
});

export default connect(mapStateToProps, null)(AlertBar);
