import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom'
import PropTypes from "prop-types";
import axios from 'axios'



const Register = (props)=>{



    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        password2: ''
    });




    // const { name, email, password, password2 } = this.state;
    const { name, email, password, password2 } = formData;


    //Maj state input fields text on change
    const onChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
    };

    const onSubmit = async (e) => {

        e.preventDefault();
        /**
         * Check matching password
         */
        if (password !== password2) {

            console.log('password no correspondance');
            props._setAlert('password no correspondance', 'danger');

        } else {

            console.log('SUCCESS', formData);
            /**
             * Create user Object
             */
                const newUser ={
                    name,
                    email,
                    password
                }

            /**
             * Async/await
             * Define headers like postman
             * Convert object javascript to object json (stringify)
             * Launch request axios.
             */
                try{
                     const config= {
                        headers: {
                            'Content-Type':'application/json'
                        }
                    }

                    const body  =  JSON.stringify(newUser);

                    let res =  await axios.post('/api/users', body, config);

                    console.log(res.data)


                } catch (error) {
                    console.error(error.response.data)
                }
        }
    }




    return (
        <Fragment>
            <h1 className="large text-primary">Sign Up</h1>
            <p className="lead"><i className="fas fa-user"></i> Create Your Account</p>

            <form className="form" onSubmit={e => onSubmit(e)} >
                <div className="form-group">
                    <input
                        type="text"
                        placeholder="Name"
                        name="name"
                        value={name}
                        onChange={e => onChange(e)}
                        required />
                </div>
                <div className="form-group">
                    <input
                        type="email"
                        placeholder="Email Address"
                        name="email"
                        value={email}
                        onChange={e => onChange(e)}
                        required
                    />
                    <small className="form-text">This site uses Gravatar so if you want a profile image, use a Gravatar email</small>
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={e => onChange(e)}
                        minLength="6"
                    />
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        name="password2"
                        value={password2}
                        onChange={e => onChange(e)}
                        minLength="6"
                    />
                </div>
                <input type="submit" className="btn btn-primary" value="Register" />
            </form>

            <p className="my-1">
                Already have an account? <Link to="/login">Sign In</Link>
            </p>
        </Fragment>
    );


}

Register.propTypes = {
}

export default Register
