import { TOGGLE_MODAL} from "../actions/types";

const initialState = false

export default function (state = initialState, action) {

    let nextState;

    switch (action.type) {

        case TOGGLE_MODAL:
            return nextState = {
                ...state,
                isOpen: !state.modal
            }      
        default:
            return nextState || state;


    }


}
