import React,{ Fragment, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Provider } from "react-redux";
import store from './store/index';
//Component
import Navbar from './components/layout/Navbar';
import ShopList from './components/ShopList';
import ShopListPreferred from './components/ShopListPreferred';
import Register from "./components/authentification/Register";
import LoginModal from './components/authentification/LoginModal';
//Actions
import { _loadUser } from "./actions/auth";
//Utils
import configTokenInHeader from "./utils/configTokenInHeader";


if (localStorage.token) {
  configTokenInHeader(localStorage.token);

}


const App = () => {


  //Definition du state : Hook[state, setState ] =  useSate(initialize)
  const [isOpen, setIsOpen] = useState({
    modal: false,

  });

  //New Hook react: (componentDidMount() in react class)
  useEffect(()=>{
    store.dispatch(_loadUser() );

  },[]);


  return(

    <Provider store={store}>

      <Router>
        <Fragment>
          <section className="container">

            <Navbar isOpenCallback />

            <Switch>
              <Route exact path="/" component={ShopList} />
              <Route exact path="/favorites-shop" component={ShopListPreferred} />
              <Route exact path="/register" component={Register} />
              <Route exact path="/login" component={LoginModal} />
            </Switch>
          </section>
          <LoginModal />

        </Fragment>
      </Router>

    </Provider>
  )
}











export default App;
