import { 
    REGISTER_FAIL,
    REGISTER_SUCCESS,
    USER_LOADED,
    AUTH_ERROR } from "../actions/types";

const initialState = {
    token: localStorage.getItem('token'),  
    isAuthenticated: null,        //Mare sure if user if connected
    loading: true,               //Make sure if user data is loaded or not
    user: null                  //Keep user data

};

export default function (state = initialState, action) {

    let nextState;  

    const { type, payload } = action;

    switch (type) {

        case REGISTER_SUCCESS:
            localStorage.setItem('token', payload.token); //add token if user signup

            return nextState = {
                    ...state ,  
                    ...payload,                      //token returned by server
                    isAuthenticated: true,
                    loading: false

                }

        case REGISTER_FAIL:
        case AUTH_ERROR:
            localStorage.removeItem('token'); //remove token

            return nextState = {
                ...state,
                token: null,                            
                isAuthenticated: false,
                loading: false
            }

        case USER_LOADED:

            return nextState = {
                ...state,
                isAuthenticated: true,
                loading:false,
                user: payload
            }


        default:
            return  state;

    }


}
