import React, { Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from "prop-types";
//Component
import ShopItem from "./ShopItem";
import SubNavbar from "./layout/SubNavbar";
import AlertNotification from "./layout/AlertNotification";
//Actions
import { _setAlert } from '../actions/alert';
import { _getMyPreferredShops } from '../actions/shop';



const ShopListPreferred = (props) => {

    /**
     * Hook React 
     * Define lifecycle component() and
     */
    useEffect(() => {

        //Define page's title
        document.title = `Preferred Shop`;

        //Load default data shop list

        //Check if the user has been redirected after an unauthorized action
        let hasAttempt = props.location.attempt;
        if (hasAttempt) {
            props._setAlert("Unauthorized : You must first be connected", "warning");
        }

    }, []) //=> specify just for mount and unmount()



    console.log('Attempted', props.location.attempt)
    console.log('shops_default', props.shops)

    /**
     * Like a shop
     * if isAuthentificated: false => call _setAlert() 
     * else call _likeShop()
     */



    /**
     * Define () that return all shops list default
     * from store 
     */
    const renderShopItem = () => {
        return props.shops.map((shop, id) => {
            return <ShopItem key={id} shop={shop} />

        })
    };

    /**
     * Make a spinner for loading store is true
     */
    const renderSpinner = () => {
        return (<div style={{ marginLeft: 473, }}>
            <img src="/images/spinner.gif" alt="spinner" />
        </div>)
    }


    return (

        <Fragment>
            <SubNavbar />

            <AlertNotification />

            <div className="site-section">
                <div className="container">
                    <div className="row">

                        <div className="row col-md-12 ">

                            {!props.loading && props.shops.length > 0 ? renderShopItem() : renderSpinner()}

                        </div>

                    </div>
                </div>
            </div>
        </Fragment>
    );
}


ShopListPreferred.propTypes = {
    _setAlert: PropTypes.func.isRequired,
    _getMyPreferredShops: PropTypes.func.isRequired,
    shops: PropTypes.array.isRequired,
    loading: PropTypes.bool,

};


// mappons le state dans les props du component
const mapStateToProps = state => ({
    shops: state.shop.preferred_shops, //not possibiility to make hoc of all list shop because ,state.shop.xxxx is different.
    loading: state.shop.loading,
});

export default connect(mapStateToProps, { _getMyPreferredShops, _setAlert })(ShopListPreferred);
