import { 
    REGISTER_FAIL,
    REGISTER_SUCCESS } from "../actions/types";

const initialState = {
    token: localStorage.getItem('token'),  
    isAuthenticated: null,        //Mare sure if user if connected
    loading: true,               //Make sure if user data is loaded or not
    user: null                  //Keep user data

};

export default function (state = initialState, action) {

    let nextState;  

    const { type, payload } = action;

    switch (type) {

        case REGISTER_SUCCESS:
            localStorage.setItem('token', payload.token); //add token if user signup

            return nextState = {
                    ...state ,  
                    ...payload,
                    isAuthenticated: true,
                    loading: false

                }

        case REGISTER_FAIL:
            localStorage.re('token', payload.token); //remove token

            return nextState = {
                ...state,
                token: null,                            //specify it.
                isAuthenticated: false,
                loading: false

            }


        default:
            return  state;

    }


}
