import React, { Fragment, useState } from 'react'
import { connect } from "react-redux";
import PropTypes from "prop-types";

import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,

} from "reactstrap";
//Components
import AlertNotification from '../layout/AlertNotification';
//Actions : _xxYYY
import { _setIsOpen } from "../../actions/modal";
import { _login } from '../../actions/auth';



const LoginModal = (props) => {



    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formModalData, setFormModalData] = useState({
        email: '',
        password: '',

    });


    // const { email, password } = this.state;
    const { email, password } = formModalData;

    const toggle = () => {
        //clear error action meth.

        //close modal
        props._setIsOpen()

    };

    //Maj state input fields text on change
    const onChange = (e) => {
        setFormModalData({
          ...formModalData,
          [e.target.name]: e.target.value
        });
    };


    const onSubmit = async (e) => {
        e.preventDefault();
        console.log(email, password)
        props._login(email, password);


    }




    return (

        <Fragment >
            <div  >
                {/* <NavLink onClick={()=>toggle()} to="#">Login</NavLink>  */}

                <Modal isOpen={props.isOpen} toggle={() => toggle()}  >

                    <ModalHeader toggle={()=>toggle()} ><span className="text-center"> SIGN IN</span>  </ModalHeader>

                    <ModalBody>

                        <AlertNotification/>

                        <Form onSubmit={e => onSubmit(e)} >
                            <FormGroup>

                                <Label for="email">Email</Label>
                                <Input
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="Email"
                                    onChange={(e)=>onChange(e) }
                                />

                                <Label for="name">Password</Label>
                                <Input
                                    type="password"
                                    name="password"
                                    id="password"
                                    placeholder="Password"
                                    onChange={(e)=>onChange(e)}
                                />
                                <br/>
                                <Button
                                    color="primary"
                                    block={true}
                                >
                                    LOGIN
                                </Button>
                            </FormGroup>
                        </Form>


                    </ModalBody>
                </Modal>
            </div>
        </Fragment>

    );


}

LoginModal.propTypes = {
    _setIsOpen: PropTypes.func.isRequired,
    _login: PropTypes.func.isRequired,
    isAuthentificate: P

};

/// mappons le state dans les props du component
const mapStateToProps = state => ({
  isOpen: state.modal,
  isAuthentificate : state.auth.isAuthentificate
});

export default connect(mapStateToProps, {_setIsOpen, _login})(LoginModal);
