import { connect } from 'react-redux';
import PropTypes from "prop-types";
//Component
import ShopItem from "./ShopItem";
import AlertNotification from "./layout/AlertNotification";
import SpinnerLoader from "./layout/SpinnerLoader";


//Actions
import { _setAlert } from '../actions/alert';
import { _getDefaultShopList, _likeShop, _unLikeShop,_getMainShopList } from '../actions/shop';


const ShopDefaultList = ({ _getDefaultShopList, _getMainShopList, _setAlert, location, default_shops, main_shops, loading, _likeShop, _unLikeShop, status, isAutenthificated }) => {
                                                                                                                                                                                     /**
                                                                                                                                                                                      * Hook React
                                                                                                                                                                                      * Define lifecycle component() and
                                                                                                                                                                                      */
                                                                                                                                                                                     useEffect(() => {
                                                                                                                                                                                       //Define page's title
                                                                                                                                                                                       if (
                                                                                                                                                                                         isAutenthificated
                                                                                                                                                                                           ? (document.title = `Main Page`)
                                                                                                                                                                                           : (document.title = `Default Page`)
                                                                                                                                                                                       );

                                                                                                                                                                                       //Load default data shop list
                                                                                                                                                                                       _getDefaultShopList();

                                                                                                                                                                                       _getMainShopList();

                                                                                                                                                                                       //Check if the user has been redirected after an unauthorized action
                                                                                                                                                                                       let hasAttempt =
                                                                                                                                                                                         location.attempt;
                                                                                                                                                                                       if (
                                                                                                                                                                                         hasAttempt
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           "Unauthorized : You must first be connected",
                                                                                                                                                                                           "warning"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                     }, []); //=> specify just for mount and unmount()

                                                                                                                                                                                     /**
                                                                                                                                                                                      * Second hook to handle
                                                                                                                                                                                      * like/unlike actions
                                                                                                                                                                                      */
                                                                                                                                                                                     useEffect(() => {
                                                                                                                                                                                       //like or dislike
                                                                                                                                                                                       if (
                                                                                                                                                                                         status.status ===
                                                                                                                                                                                         200
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           "Success: successful operation... ",
                                                                                                                                                                                           "success"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                       //Shop already like or dislike
                                                                                                                                                                                       if (
                                                                                                                                                                                         status.msg !==
                                                                                                                                                                                           null &&
                                                                                                                                                                                         status.status ===
                                                                                                                                                                                           409
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           status.msg,
                                                                                                                                                                                           "dark"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                       //shop not exist
                                                                                                                                                                                       if (
                                                                                                                                                                                         status.msg !==
                                                                                                                                                                                           null &&
                                                                                                                                                                                         status.status ===
                                                                                                                                                                                           404
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           status.msg,
                                                                                                                                                                                           "danger"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                       //Server error
                                                                                                                                                                                       if (
                                                                                                                                                                                         status.msg !==
                                                                                                                                                                                           null &&
                                                                                                                                                                                         status.status ===
                                                                                                                                                                                           500
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           "Echec",
                                                                                                                                                                                           "danger"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                     }, [
                                                                                                                                                                                       status,
                                                                                                                                                                                       _setAlert
                                                                                                                                                                                     ]);

                                                                                                                                                                                     console.log(
                                                                                                                                                                                       "Attempted",
                                                                                                                                                                                       location.attempt
                                                                                                                                                                                     );

                                                                                                                                                                                     /**
                                                                                                                                                                                      * LIKE A SHOP
                                                                                                                                                                                      * if isAuthentificated: false => call _setAlert()
                                                                                                                                                                                      * else call _likeShop()
                                                                                                                                                                                      */
                                                                                                                                                                                     const handleLiked = (
                                                                                                                                                                                       e,
                                                                                                                                                                                       id
                                                                                                                                                                                     ) => {
                                                                                                                                                                                       e.preventDefault();
                                                                                                                                                                                       console.log(
                                                                                                                                                                                         id
                                                                                                                                                                                       );

                                                                                                                                                                                       if (
                                                                                                                                                                                         !isAutenthificated
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _likeShop(
                                                                                                                                                                                           id
                                                                                                                                                                                         );
                                                                                                                                                                                       } else {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           "Unauthorized : You must first be connect",
                                                                                                                                                                                           "warning"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                     };

                                                                                                                                                                                     /**
                                                                                                                                                                                      * UNLIKE A SHOP
                                                                                                                                                                                      * if isAuthentificated: false => call _setAlert()
                                                                                                                                                                                      * else call _unlikeShop()
                                                                                                                                                                                      */
                                                                                                                                                                                     const handleUnLiked = (
                                                                                                                                                                                       e,
                                                                                                                                                                                       id
                                                                                                                                                                                     ) => {
                                                                                                                                                                                       e.preventDefault();
                                                                                                                                                                                       console.log(
                                                                                                                                                                                         id
                                                                                                                                                                                       );

                                                                                                                                                                                       if (
                                                                                                                                                                                         !isAutenthificated
                                                                                                                                                                                       ) {
                                                                                                                                                                                         _unLikeShop(
                                                                                                                                                                                           id
                                                                                                                                                                                         );
                                                                                                                                                                                       } else {
                                                                                                                                                                                         _setAlert(
                                                                                                                                                                                           "Unauthorized : You must first be connect",
                                                                                                                                                                                           "warning"
                                                                                                                                                                                         );
                                                                                                                                                                                       }
                                                                                                                                                                                     };

                                                                                                                                                                                     /**
                                                                                                                                                                                      * Define shop variable
                                                                                                                                                                                      * And we assignment
                                                                                                                                                                                      */
                                                                                                                                                                                     let shops = [];
                                                                                                                                                                                     if (
                                                                                                                                                                                       !isAutenthificated
                                                                                                                                                                                         ? (shops = main_shops)
                                                                                                                                                                                         : (shops = default_shops)
                                                                                                                                                                                     );

                                                                                                                                                                                     /**
                                                                                                                                                                                      * Define () that return
                                                                                                                                                                                      * all shops list default from store OR
                                                                                                                                                                                      * all shops list without the shops already liked
                                                                                                                                                                                      */
                                                                                                                                                                                     const renderShopItem = () => {
                                                                                                                                                                                       return shops.map(
                                                                                                                                                                                         (
                                                                                                                                                                                           shop,
                                                                                                                                                                                           id
                                                                                                                                                                                         ) => {
                                                                                                                                                                                           return (
                                                                                                                                                                                             <ShopItem
                                                                                                                                                                                               key={
                                                                                                                                                                                                 id
                                                                                                                                                                                               }
                                                                                                                                                                                               shop={
                                                                                                                                                                                                 shop
                                                                                                                                                                                               }
                                                                                                                                                                                               handleLikeCallback={
                                                                                                                                                                                                 handleLiked
                                                                                                                                                                                               }
                                                                                                                                                                                               handleUnLikeCallback={
                                                                                                                                                                                                 handleUnLiked
                                                                                                                                                                                               }
                                                                                                                                                                                             />
                                                                                                                                                                                           );
                                                                                                                                                                                         }
                                                                                                                                                                                       );
                                                                                                                                                                                     };

                                                                                                                                                                                     return (
                                                                                                                                                                                       <Fragment>
                                                                                                                                                                                         <AlertNotification />

                                                                                                                                                                                         <div className="site-section">
                                                                                                                                                                                           <div className="container">
                                                                                                                                                                                             <h1>
                                                                                                                                                                                               {" "}
                                                                                                                                                                                               Tittre
                                                                                                                                                                                             </h1>
                                                                                                                                                                                             <div className="row">
                                                                                                                                                                                               <div className="row col-md-12 ">
                                                                                                                                                                                                 {!loading &&
                                                                                                                                                                                                 shops.length >
                                                                                                                                                                                                   0 ? (
                                                                                                                                                                                                   renderShopItem()
                                                                                                                                                                                                 ) : (
                                                                                                                                                                                                   <SpinnerLoader />
                                                                                                                                                                                                 )}
                                                                                                                                                                                               </div>

                                                                                                                                                                                               <div
                                                                                                                                                                                                 className="row"
                                                                                                                                                                                                 data-aos="fade-up"
                                                                                                                                                                                               >
                                                                                                                                                                                                 <div className="col-md-12 text-center">
                                                                                                                                                                                                   <div className="site-block-27">
                                                                                                                                                                                                     <ul>
                                                                                                                                                                                                       <li>
                                                                                                                                                                                                         <Link to="#">
                                                                                                                                                                                                           &lt;
                                                                                                                                                                                                         </Link>
                                                                                                                                                                                                       </li>
