import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom'
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,
  NavLink,
  Alert
} from "reactstrap";


//Components
//Actions : _xxYYY




const LoginModal = (props) => {



    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formModalData, setFormModalData] = useState({
        name: '',
        email: '',
        password: '',
        modal: false,

    });


    // const { email, password } = this.state;
    const { email, password } = formModalData;

    const toggle = () => {
        //clear error action meth.
        this.props._clearErrors();

        this.setState({ modal: !this.state.modal });
    };
    //Maj state input fields text on change
    const onChange = (e) => {
        setFormModalData({
          ...formModalData,
          [e.target.name]: e.target.value
        });
    };


    const onSubmit = async (e) => {
        e.preventDefault();


    }




    return (

        <Fragment>

            <div className="row">
                <div className="col-md-6 ">
                    <br /><br />

                    <form onSubmit={e => onSubmit(e)} noValidate>
                        <div className="p-3 p-lg-5 border">

                            <h3>Sign in</h3>



                            <div className="form-group row">
                                <div className="col-md-12">
                                    <label htmlFor="email" className="text-black">
                                        Email <span className="text-danger">*</span>
                                    </label>
                                    <input
                                        type="email"
                                        className="form-control"
                                        name="email"
                                        value={email}
                                        onChange={e => onChange(e)}
                                        required
                                    />
                                </div>
                            </div>

                            <div className="form-group row">
                                <div className="col-md-12">
                                    <label htmlFor="password" className="text-black">
                                        Password <span className="text-danger">*</span>
                                    </label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        name="password"
                                        value={password}
                                        onChange={e => onChange(e)}
                                        required
                                    />
                                </div>
                            </div>

                            <Button type="submit" color="primary" size="lg" block>LOGIN</Button>

                            <div className="form-group row">
                                <div className="col-lg-12">
                                    <input
                                        type="submit"
                                        className="btn btn-primary btn-lg btn-block"
                                        value="LOGIN"
                                    />
                                </div>
                            </div>

                        </div>

                    </form>

                </div>



            </div>

        </Fragment>

    );


}


export default LoginModal
