import React, { Fragment, useState } from 'react'
import { Link ,Redirect} from 'react-router-dom'
import { connect } from "react-redux";
import PropTypes from "prop-types";
//Components
import Alert from '../layout/AlertNotification';
//Actions : _xxYYY
import { _setAlert } from '../../actions/alert';
import { _register } from '../../actions/auth';



const Register = (props)=>{

    const { isAuthentificate } = props;


    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        password2: ''
    });




    // const { name, email, password, password2 } = this.state;
    const { name, email, password, password2 } = formData;


    //Maj state input fields text on change
    const onChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
    };


    const onSubmit = async (e) => {
        e.preventDefault();

        //Check matching password
        if (password !== password2) {
            props._setAlert('Passwords do not match', 'danger'); //call action redux
            console.log('Register','password no correspondance');

        } else {
            props._register({ name, email, password }); //call action redux
            console.log('Register', formData);

        }
    }


    //Redirect if logged in
    if (isAuthentificate) {
        console.log(isAuthentificate)

        return <Redirect to="/" />

    }



    // Similaire à componentDidMount et componentDidUpdate :
    useEffect(() => {
        // Met à jour le titre du document via l’API du navigateur
        document.title = `Vous avez cliqué ${count} fois`;
    });

    return (
    <Fragment>

        <div className="row">
            <div className="col-md-6 ">
            <br/><br/>

            <form  onSubmit={e => onSubmit(e)} noValidate >
                <div className="p-3 p-lg-5 border">

                <h3>Sign up</h3>

                <div className="form-group row">
                    <div className="col-md-12">
                    <label htmlFor="name" className="text-black">
                        Name <span className="text-danger">*</span>
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={name}
                        placeholder="Your name"
                        onChange={e => onChange(e)}
                        required
                    />
                    </div>
                </div>

                <div className="form-group row">
                    <div className="col-md-12">
                    <label htmlFor="email" className="text-black">
                        Email <span className="text-danger">*</span>
                    </label>
                    <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={email}
                        onChange={e => onChange(e)}
                        required
                    />
                    </div>
                </div>

                <div className="form-group row">
                    <div className="col-md-12">
                    <label htmlFor="password" className="text-black">
                        Password <span className="text-danger">*</span>
                    </label>
                    <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={password}
                        onChange={e => onChange(e)}
                        required
                    />
                    </div>
                </div>


                <div className="form-group row">
                    <div className="col-md-12">
                    <label htmlFor="c_email" className="text-black">
                        Password2 <span className="text-danger">*</span>
                    </label>
                    <input
                        type="password"
                        className="form-control"
                        name="password2"
                        value={password2}
                        onChange={e => onChange(e)}
                        required
                    />
                    </div>
                </div>

                <div className="form-group row">
                    <div className="col-lg-12">
                    <input
                        type="submit"
                        className="btn btn-primary btn-lg btn-block"
                        value="REGISTER"
                    />
                    </div>
                </div>


                    <p className="my-1">
                        Already have an account? <Link to="/login">Sign In</Link>
                    </p>
                </div>

            </form>

            </div>

            <div className="col-md-6" >
                <br /><br />
                <Alert/>
            </div>

        </div>

    </Fragment>

    );


}

Register.propTypes = {
    _setAlert: PropTypes.func.isRequired,
    _register: PropTypes.func.isRequired,
    isAuthentificate: PropTypes.bool,


}

/// mappons le state dans les props du component
const mapStateToProps = state => ({
    isAuthentificate: state.auth.isAuthentificate,

});

export default connect(mapStateToProps, { _setAlert, _register })(Register)
