        }
    }





    return (

        <Fragment>
            <SubNavbar />

            <AlertNotification />

            <div className="site-section">
                <div className="container">
                    <div className="row">

                        <div className="row col-md-12 ">


                            {/* if authenticated =true && shop === null */}
                            {!isAutentificated && props.shops === null ? props._setAlert("You still have nothing in your favorites.", 'primary') : renderShopItem()   }
                            {/* if no authentificated  */}
                            {props.isAutentificated ? renderShopItem() : props._setAlert("You must be first connected.", 'warning')}


                            { !props.loading ? null : renderSpinner() }



                        </div>

                    </div>
                </div>
            </div>
        </Fragment>
    );
}


ShopListPreferred.propTypes = {
    _setAlert: PropTypes.func.isRequired,
    _getMyPreferredShops: PropTypes.func.isRequired,
    shops: PropTypes.array.isRequired,
    loading: PropTypes.bool,
    isAuthentificated: PropTypes.func.isRequired,

};


// mappons le state dans les props du component
const mapStateToProps = state => ({
  shops: state.shop.preferred_shops, //not possibiility to make hoc of all list shop because ,state.shop.xxxx is different.
  loading: state.shop.loading,
  isAuthentificated: state.auth.isAuthentificated
});

export default connect(mapStateToProps, { _getMyPreferredShops, _setAlert })(ShopListPreferred);
