import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom'
import { connect } from "react-redux";
import PropTypes from "prop-types";

import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,
  NavLink,
} from "reactstrap";
//Components
import AlertNotification from '../layout/AlertNotification';
//Actions : _xxYYY
import { _setIsOpen } from "../../actions/modal";



const divStyle = {
    backgroundColor: 'blue',
    position: 'absolue', zIndex: '1',
    bott: 444
};


const LoginModal = (props) => {



    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formModalData, setFormModalData] = useState({
        name: '',
        email: '',
        password: '',

    });


    // const { email, password } = this.state;
    const { email, password } = formModalData;

    const toggle = () => {
        //clear error action meth.

        //close modal
        props._setIsOpen()

    };

    //Maj state input fields text on change
    const onChange = (e) => {
        setFormModalData({
          ...formModalData,
          [e.target.name]: e.target.value
        });
    };


    const onSubmit = async (e) => {
        e.preventDefault();

    }




    return (

        <Fragment >
            <div style={divStyle} >
                <NavLink onClick={()=>toggle()} to="#">Login</NavLink> 

                <Modal isOpen={props.isOpen} toggle={()=>toggle()} >

                    <ModalHeader toggle={()=>toggle()}> Login </ModalHeader>

                    <ModalBody>

                        <AlertNotification/>

                        <Form onSubmit={e => onSubmit(e)} >
                            <FormGroup>

                                <Label for="email">Email</Label>
                                <Input
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="Email"
                                    onChange={(e)=>onChange(e) }
                                />

                                <Label for="name">Password</Label>
                                <Input
                                    type="password"
                                    name="password"
                                    id="password"
                                    placeholder="Password"
                                    onChange={(e)=>onChange(e)}
                                />
                                <br/>
                                <Button
                                    color="primary"
                                    block={true}
                                >
                                    LOGIN
                                </Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        </Fragment>

    );


}

LoginModal.propTypes = {
    _setIsOpen: PropTypes.func.isRequired
};

/// mappons le state dans les props du component
const mapStateToProps = state => ({
  isOpen: state.modal
});

export default connect(mapStateToProps, {_setIsOpen})(LoginModal);
