import axios from 'axios';
import {
  GET_DEFAULT_LIST_SHOPS,
  GET_PREFERRED_SHOPS,
  GET_NEARBY_SHOPS,
  REMOVE_SHOPS_USER_RELATED,
  ERROR_SHOPS
} from "./types";
import { API_URI } from '../utils/uri';



/**
 * Default shop list.
 * without criterias
 */
export const _getDefaultShopList = () => async dispatch => {

    try {
        const response = await axios.get(`${API_URI}/default`);

        dispatch({
          type: GET_DEFAULT_LIST_SHOPS,
          payload: response.data
        });  

    } catch (error) {

        dispatch({
            type: ERROR_SHOPS,
            payload: { msg: error.response.statusText, status: error.response.status }
        })
    }
}



/**
 * Get preferred shop of user connected
 * usermust connected
 */
export const _getMyPreferredShops = () => async dispatch => {

    try {
        const response = await axios.get(`${API_URI}/preferred`);

        dispatch({
            type:GET_PREFERRED_SHOPS,
            payload: response.data
        });

    } catch (error) {

        dispatch({
            type:ERROR_SHOPS,
            payload: { msg: error.response.statusText , status: error.response.status }
        })
    }

};
